import React from 'react';
import style from "./Promotion.module.css";



type bannerType = {
    src: string,
    id: number
}

const Promotion = (props: any) => {


    return (

    <div className="wrapper" id='promotion'>
        <div className="promotion">
            <h2>Акции</h2>
            <div className="grid">
                {
                props.banners.map(
                    (banner: bannerType)=>{
                        <a href="" className='popup_link'><img key={banner.id} src={`${process.env.PUBLIC_URL}/assets${banner.src}`} alt=""/></a>
                    }
                )
                }

            </div>
        </div>
    </div>
    );
}

export default Promotion;
