import React from 'react';
import {Swiper, SwiperSlide} from "swiper/react";
import {Pagination, Autoplay} from "swiper";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

type bannerType = {
    src: string,
    id: number

}
let banners_all: Array<bannerType> = [
    {
        src: "/banners/1.png",
        id: 1
    },
    {
        src: "/banners/2.png",
        id: 2
    },
    {
        src: "/banners/3.png",
        id: 3
    },
    {
        src: "/banners/4.png",
        id: 4
    },
    {
        src: "/banners/5.png",
        id: 5
    },
    {
        src: "/banners/6.png",
        id: 6
    },
    {
        src: "/banners/7.png",
        id: 7
    },
    {
        src: "/banners/8.png",
        id: 8
    }
]



const Banners = () => {
    return (
        <Swiper slidesPerView={1}
                             spaceBetween={30}
                             loop={true}
                             pagination={{
                                 clickable: true,
                             }}

                             autoplay={{
                                 delay: 2500,
                                 disableOnInteraction: false,
                             }}
                             modules={[Pagination, Autoplay]}
                             className="mySwiper">
            {
                banners_all.map((banner: bannerType) => {
                    return <SwiperSlide>
                               <img key={banner.id} src={`${process.env.PUBLIC_URL}/assets${banner.src}`} alt=""/>
                           </SwiperSlide>;})
            }
        </Swiper>
    );
}

export default Banners;
